package first01;

public class BooleanOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean trueValue = true;
		boolean falseValue  = false;
		//!
		System.out.println("!trueValue="+!trueValue);
		System.out.println("!falseValue="+!falseValue);
		//&&
		System.out.println("trueValue&&falseValue="+(trueValue&&falseValue));
		System.out.println("falseValue&&true="+(falseValue&&true));
		System.out.println("trueValue&&false"+(trueValue&&false));
		System.out.println("falseValue&&false"+(falseValue&&false));
		//||
		System.out.println("trueValue||true="+(trueValue||true));
		System.out.println("falseValue||true="+(falseValue||true));
		System.out.println("trueValue||false"+(trueValue||false));
		System.out.println("falseValue||false"+(falseValue||false));
		System.out.println("\n");
		System.out.println("ÕıŒƒ’‹"+"20210314140");

		
		
	}

}
