package test01;

public class ByteOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 9;
		int num2 = 7;
		int fei = ~num1;
		int huo = num1 | num2;
		int yu = num1 & num2;
		int yiHuo = num1 ^ num2;
		int youYi = num1>>1;
		int zuoYi = num1 <<1;
		int xYouYi = num1>>>1;
		System.out.println(fei);
		System.out.println(huo);
		System.out.println(yu);
		System.out.println(yiHuo);
		System.out.println(youYi);
		System.out.println(zuoYi);
		System.out.println(xYouYi);
		
		

	}

}
