package test01;

public class NineTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//注意print和println之间的区别
		for(int i=1;i<=9;i++){
			for(int j=1;j<=i;j++){
				System.out.print(j+"*"+i+"="+i*j+"");
				
			}
			
			System.out.println();
			
		}
		System.out.println("王文哲"+"20210310140");
		
	}

}
